import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faInfoCircle } from '@fortawesome/free-solid-svg-icons';

function Userdata() {
    const [userData, setUserdata] = useState([]);
    const [selectedItems, setSelectedItems] = useState([]);
    const [selectedUserData, setSelectedUserData] = useState(null); // State to hold selected user data for modal

    useEffect(() => {
        const getUserdata = async () => {
            const reqData = await fetch("http://localhost:8080/student");
            const resData = await reqData.json();

            setUserdata(resData);
        }
        getUserdata();
    }, []);

    const handleCheckboxChange = (index) => {
        const isSelected = selectedItems.includes(index);
        let updatedSelectedItems;

        if (!isSelected) {
            updatedSelectedItems = [...selectedItems, index];
        } else {
            updatedSelectedItems = selectedItems.filter(item => item !== index);
        }
        setSelectedItems(updatedSelectedItems);

        const selectedUserData = updatedSelectedItems.map(i => userData[i]);
        console.log("Selected UserData:", selectedUserData);
    };


    const handleDelete = async(id) => {
       await fetch(`http://localhost:8080/student?id=${id}`, {
            method: 'DELETE',
        })

        setTimeout(()=>{
            window.location.reload();
        }, 1000);
    };

    // Function to handle modal opening and setting selected user data
    const handleModalOpen = (id) => {
        const selectedUser = userData.find(user => user.id === id);
        setSelectedUserData(selectedUser);
    }

    // Modal close handler
    const handleCloseModal = () => {
        setSelectedUserData(null);
    }

    return (
        <React.Fragment>
            <div className="container">
                <div className="row">
                    <div className="col-md-12">
                        <h5 className="mt-2">Öğrenci Listesi</h5>
                        <div className="d-grid d-md-flex justify-content-md-end mb-3">
                            <Link to="/addStudent" className="btn btn-warning">Öğrenci Ekle</Link>
                            <button type="button" className="btn btn-primary" onClick={() => handleDelete(userData.id)}>Ada göre Sırala</button>
                        </div>
                        <table className="table table-bordered table-striped">
                            <thead>
                                <tr>
                                    <th>Sıra</th>
                                    <th></th>
                                    <th>Adı</th>
                                    <th>Soyadı</th>
                                    <th>Vize Notu</th>
                                    <th>Final Notu</th>
                                    <th>Harf Notu</th>
                                    <th>Option</th>
                                </tr>
                            </thead>
                            <tbody>
                                {userData.map((userData, index) => (
                                    <tr key={index}>
                                        <td>{index + 1}</td>
                                        <td>
                                            <input type="checkbox" onChange={() => handleCheckboxChange(index)} checked={selectedItems.includes(index)} />
                                        </td>
                                        <td>{userData.name}</td>
                                        <td>{userData.familyName}</td>
                                        <td>{userData.midtermGrade}</td>
                                        <td>{userData.finalGrade}</td>
                                        <td>{userData.letterGrade}</td> 

                                       <td>
                                        <div style={{ display: 'flex', alignItems: 'center' }}>
                                         <Link to={"/editUser/" + userData.id} className="btn btn-success mx-2">Edit</Link>
                                        <button type="button" className="btn btn-danger" onClick={() => handleDelete(userData.id)}>Sil</button>
                                                <div style={{ marginLeft: '0.5rem', cursor: 'pointer' }} onClick={() => handleModalOpen(userData.id)}>
                                                  <FontAwesomeIcon icon={faInfoCircle} />
                                                 </div>
                                        </div>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
            {/* Modal for displaying selected user data */}
            {selectedUserData && (
                <div className="modal">
                    <div className="modal-content">
                        <span className="close" onClick={handleCloseModal}>&times;</span>
                        <h2>Öğrenci Detayları</h2>
                        <p><strong>Adı:</strong> {selectedUserData.name}</p>
                        <p><strong>Soyadı:</strong> {selectedUserData.familyName}</p>
                        <p><strong>Vize Notu:</strong> {selectedUserData.midtermGrade}</p>
                        <p><strong>Final Notu:</strong> {selectedUserData.finalGrade}</p>
                        <p><strong>Harf Notu:</strong> {selectedUserData.letterGrade}</p>
                    </div>
                </div>
            )}
        </React.Fragment>
    );
}

export default Userdata;
