import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

function Userdata() {
    const [userData, setUserdata] = useState([]);
    const [selectedItems, setSelectedItems] = useState([]);

    useEffect(() => {
        const getUserdata = async () => {
            const reqData = await fetch("http://localhost:8080/student");
            const resData = await reqData.json();
            setUserdata(resData);
        }
        getUserdata();
    }, []);

    const handleCheckboxChange = (index) => {
        const selectedIndex = selectedItems.indexOf(index);
        if (selectedIndex === -1) {
            setSelectedItems([...selectedItems, index]);
        } else {
            const updatedSelectedItems = [...selectedItems];
            updatedSelectedItems.splice(selectedIndex, 1);
            setSelectedItems(updatedSelectedItems);
        }
        // Select or deselect corresponding userData object
        const selectedUserData = selectedItems.indexOf(index) === -1
            ? [...userData, userData[index]]
            : userData.filter((_, i) => i !== index);
        console.log("Selected UserData:", selectedUserData);
    };

    return (
        <React.Fragment>
            <div className="container">
                <div className="row">
                    <div className="col-md-12">
                        <h5 className="mt-2">Öğrenci Listesi</h5>
                        <div className="d-grid d-md-flex justify-content-md-end mb-3">
                            <Link to="/addStudent" className="btn btn-warning">Öğrenci Ekle</Link>
                        </div>
                        <table className="table table-bordered table-striped">
                            <thead>
                                <tr>
                                    <th>Sıra</th>
                                    <th></th>
                                    <th>Adı</th>
                                    <th>Soyadı</th>
                                    <th>Vize Notu</th>
                                    <th>Final Notu</th>
                                    <th>Harf Notu</th>
                                    <th>option</th>
                                </tr>
                            </thead>
                            <tbody>
                                {userData.map((userData, index) => (
                                    <tr key={index}>
                                        <td>{index + 1} </td>
                                        <td> <input type="checkbox" onChange={() => handleCheckboxChange(index)} /> </td>
                                        <td>{userData.name} </td>
                                        <td>{userData.familyName} </td>
                                        <td>{userData.grade} </td>
                                        <td>{userData.grade} </td>
                                        <td>{userData.letterGrade} </td>
                                        <td>
                                            <Link to={"/editUser/" + userData.userid} className="btn btn-success mx-2">Edit</Link>
                                            <Link to="/deleteUser" className="btn btn-danger">Delete</Link>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </React.Fragment>
    );
}

export default Userdata;
