import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faInfoCircle } from '@fortawesome/free-solid-svg-icons';
import './modal.css';

function Userdata() {
    const [userData, setUserdata] = useState([]);
    const [selectedItems, setSelectedItems] = useState([]);
    const [selectedUserId, setSelectedUserId] = useState(null);
    const [showPopup, setShowPopup] = useState(false);
    const [additionalUserData, setAdditionalUserData] = useState(null);

    useEffect(() => {
        const getUserdata = async () => {
            const reqData = await fetch("http://localhost:8080/student");
            const resData = await reqData.json();

            setUserdata(resData);
        }
        getUserdata();
    }, []);

    const handleCheckboxChange = (index) => {
        const isSelected = selectedItems.includes(index);
        let updatedSelectedItems;

        if (!isSelected) {
            updatedSelectedItems = [...selectedItems, index];
        } else {
            updatedSelectedItems = selectedItems.filter(item => item !== index);
        }
        setSelectedItems(updatedSelectedItems);

        const selectedUserData = updatedSelectedItems.map(i => userData[i]);
        console.log("Selected UserData:", selectedUserData);
    };

    const handleDelete = async(id) => {
       await fetch(`http://localhost:8080/student?id=${id}`, {
            method: 'DELETE',
        })

        setTimeout(()=>{
            window.location.reload();
        }, 1000);
    };

    const handleInfoClick = async (id) => {
        setSelectedUserId(id);
        setShowPopup(true);

        const reqData = await fetch(`http://localhost:8080/student/byid?id=${id}`);
        const userData = await reqData.json();
        setAdditionalUserData(userData);
    };

    return (
        <React.Fragment>
            <div className="container">
                <div className="row">
                    <div className="col-md-12">
                        <h5 className="mt-2">Öğrenci Listesi</h5>
                        <div className="d-grid d-md-flex justify-content-md-end mb-3">
                            <Link to="/addStudent" className="btn btn-warning">Öğrenci Ekle</Link>
                            <div className="dropdown">
                                <button className="btn btn-primary dropdown-toggle" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                                    Dropdown Ekle
                                </button>
                                <div className="dropdown-menu" aria-labelledby="dropdownMenuButton">
                                    <button className="dropdown-item" type="button">Seçenek 1</button>
                                    <button className="dropdown-item" type="button">Seçenek 2</button>
                                    <button className="dropdown-item" type="button">Seçenek 3</button>
                                </div>
                            </div>
                        </div>
                        <table className="table table-bordered table-striped">
                            <thead>
                                <tr>
                                    <th>Sıra</th>
                                    <th> DropDown Ekle</th>
                                    <th>Adı</th>
                                    <th>Soyadı</th>
                                    <th>Vize Notu</th>
                                    <th>Final Notu</th>
                                    <th>Harf Notu</th>
                                    <th>Option</th>
                                </tr>
                            </thead>
                            <tbody>
                                {userData.map((userData, index) => (
                                    <tr key={index}>
                                        <td>{index + 1}</td>
                                        <td>
                                            <input type="checkbox" onChange={() => handleCheckboxChange(index)} checked={selectedItems.includes(index)} />
                                        </td>
                                        <td>{userData.name}</td>
                                        <td>{userData.familyName}</td>
                                        <td>{userData.midtermGrade}</td>
                                        <td>{userData.finalGrade}</td>
                                        <td>{userData.letterGrade}</td> 

                                       <td>
                                        <div style={{ display: 'flex', alignItems: 'center' }}>
                                         <Link to={"/editUser/" + userData.id} className="btn btn-success mx-2">Edit</Link>
                                        <button type="button" className="btn btn-danger" onClick={() => handleDelete(userData.id)}>Sil</button>
                                                <div style={{ marginLeft: '0.5rem', cursor: 'pointer' }} onClick={() => handleInfoClick(userData.id)}>
                                                  <FontAwesomeIcon icon={faInfoCircle} />
                                                 </div>
                                        </div>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
            {showPopup && (
                <div className="popup">
                    <div className="popup-inner">
                        <button className="close-btn" onClick={() => setShowPopup(false)}>Close</button>
                        {additionalUserData && (
                            <div>
                                <p>Yaş: {additionalUserData.age}</p>
                                <p>Telefon Numarası: {additionalUserData.phoneNo}</p>
                                <p>Adres: {additionalUserData.address}</p>
                            </div>
                        )}
                    </div>
                </div>
            )}
        </React.Fragment>
    );
}

export default Userdata;
